-- -----------------------------
-- Table structure for `ey_article_content`
-- -----------------------------
DROP TABLE IF EXISTS `ey_article_content`;
CREATE TABLE `ey_article_content` (
  `id` int(10) NOT NULL AUTO_INCREMENT,
  `aid` int(10) DEFAULT '0' COMMENT '文档ID',
  `content` longtext COMMENT '内容详情',
  `add_time` int(11) DEFAULT '0' COMMENT '新增时间',
  `update_time` int(11) DEFAULT '0' COMMENT '更新时间',
  PRIMARY KEY (`id`),
  KEY `news_id` (`aid`) USING BTREE
) ENGINE=MyISAM AUTO_INCREMENT=46 DEFAULT CHARSET=utf8 COMMENT='资讯内容表_存储正常内容';

-- -----------------------------
-- Records of `ey_article_content`
-- -----------------------------
INSERT INTO `ey_article_content` VALUES ('38', '63', '&lt;p&gt;开一间风格雅致、环境舒适的咖啡店，是很多年轻人的梦想。&lt;/p&gt;&lt;p&gt;　　但是，有情怀、懂生活，就能把咖啡店经营好吗?NO……做生意拼的的是综合实力，来不得半点虚假。&lt;/p&gt;&lt;p&gt;　　理想中咖啡店云淡风轻，现实中则是一场没有硝烟的战争。&lt;/p&gt;&lt;p&gt;　　市场研究公司 Mintel 最新发布的报告显示，2017 年美国市场的咖啡店数量接近 3.3 万。&lt;/p&gt;&lt;p&gt;　　如今美国市场的咖啡店数量比五年前增加了 16%。&lt;/p&gt;&lt;p&gt;　　同行竞争正在变得愈发激烈。&lt;/p&gt;&lt;p&gt;　　当麦当劳、唐恩都乐、以及更多超市都开始提供咖啡，开一家咖啡馆越来越难。&lt;/p&gt;&lt;p&gt;　　中国市场也面临类似的情况，星巴克等巨头们在攻城略地，连咖啡、luckin coffee(瑞幸咖啡)等新秀来势汹汹。&lt;/p&gt;&lt;p&gt;　　从麦当劳、肯德基等餐饮巨头，到7-11、全家等便利店，都想在咖啡市场分一杯羹。&lt;/p&gt;&lt;p&gt;　　不要说独立咖啡馆，即使资金雄厚的连锁咖啡馆，也经常遭遇经营困境。&lt;/p&gt;&lt;p&gt;　　比如前几年风靡一时的咖啡陪你，2015年前后就在中国陷入“关店危机”。&lt;/p&gt;&lt;p&gt;　　去年，咖啡陪你创始人，素有“咖啡王”之称的韩国KH公司代表姜勋(音)在家中自杀。&lt;/p&gt;&lt;p&gt;　　无论是咖啡连锁公司，还是独立咖啡店的面临的市场竞争都日趋激烈。&lt;/p&gt;&lt;p&gt;　　那么作为没钱没资源的中小创业者，真的就不能开咖啡馆吗?&lt;/p&gt;&lt;p&gt;　　当然不是，但必须抛弃原来的开店模式，改用尽量压缩成本，服务目标客户的新打法。&lt;/p&gt;&lt;p&gt;　　在山东德州有一家小咖啡店，总投资不超过10万，12平米的店，老板自己1个人经营。&lt;/p&gt;&lt;p&gt;　　开业第1个月就盈利，每个月营业额都稳定上升，刚过去的4月，营业额比3月又上升了30%。&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;', '1527673179', '1527673282');
INSERT INTO `ey_article_content` VALUES ('39', '64', '&lt;p&gt;咖啡，意为“力量与热情”，目前已与茶叶、可可并称当今世界三大饮料植物，是全球仅次于原油的第二大国际大宗贸易商品。虽然咖啡市场变幻莫测、风起云涌，但咖啡行业的整体规模一直呈现快速增长的良好态势。但根据ICO(国际咖啡协会)数据显示，2017年～2018年全球咖啡产量为15966万袋，比上一年度增长了1.2%。2018年2月，世界咖啡出口达到993万袋，同比增长0.4%，巴西、越南、印尼等主产国占了世界咖啡产量的70%。&lt;/p&gt;&lt;p&gt;&lt;img alt=&quot;&quot; src=&quot;/public/upload/remote/2018/05/30/5b0e71848907c.png&quot;/&gt;&lt;/p&gt;&lt;p&gt;　　从全球来看，巴西咖啡产量居世界之首，而美国咖啡消费量居世界第一，年消费约3万人民币。虽然我国每人每年消费的咖啡大约仅4杯，但整个行业却每年以25%的消费速度增长，远高于世界2.5%的平均增长率，是世界咖啡消费增长最快的国家之一。&lt;/p&gt;&lt;p&gt;　　而在去年7月，星巴克以大约13亿美元的现金，收购了华东市场合资企业剩余50%股份，实现了星巴克在中国大陆市场的全面直营。&lt;/p&gt;&lt;p&gt;　　10月初，英国咖啡公司costa完成了与江苏悦达集团在中国合资的企业悦达咖世家的股份并购，使costa在中国全资拥有中国南方市场。两大巨头对中国咖啡市场的野心，无意间也给中国投资者打了一针强心剂。&lt;/p&gt;&lt;p&gt;　　先是以外卖平台起家的连咖啡获得B+轮1.5亿融资，上海精品咖啡品牌seesaw获得4500万元投资，自助咖啡机有饮、小咖纷纷获投，原神州租车团队更是斥10亿巨资打造咖啡新零售…….一时间，咖啡行业风起云涌，竞争激烈，而疯狂的追逐背后时候会产生新一轮的泡沫?咖啡为何会在今年迎来爆发期?今后又将走向何方?&lt;/p&gt;&lt;p&gt;　　为什么是咖啡?&lt;/p&gt;&lt;p&gt;　　自神农尝百草，饮茶解毒开始，中国人饮茶的历史由来已久，中国既是茶的故乡，也是茶文化的发祥地，但咖啡为什么在中国会有机会?首先咖啡是继茶饮、可可之后全球最大的饮品，它之所以能被传播开来，是因为途经人类文明发源地。咖啡本是非洲埃塞俄比亚原产的一种植物，非洲与中东(现在的伊拉克、约旦、叙利亚、以色列等几个地方)接壤来往频繁，随后咖啡被阿拉伯人带入中东，经过了漫长的发展，你会看到有一种喝法叫“土耳其咖啡”，先将咖啡豆磨完以后直接冲，然后把咖啡渣和水混在一起喝，与现在的流行喝法完全不一样。&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;', '1527673213', '1527673213');
INSERT INTO `ey_article_content` VALUES ('40', '65', '&lt;p&gt;最近，咖啡行业发生哪些新鲜事呢?一些精品咖啡品牌获得融资，星巴克忙着布局咖啡高端市场，而麦当劳旗下的麦咖啡(McCafé)推出了新的咖啡产品“咖啡厅质量”海龟。&lt;/p&gt;&lt;p&gt;　　本周三，麦当劳宣布，麦咖啡计划推出新的季节咖啡系列“咖啡厅质量”海龟(&amp;quot;café quality” Turtle)，包括一款玛奇朵咖啡(冷/热)和一款冰咖啡。玛奇朵咖啡比较简单，就是浓缩咖啡加上蒸汽牛奶，冰咖啡则是一种巧克力焦糖榛子味的浓缩咖啡饮品。它们将于4月初出现在麦咖啡的菜单上，并限时供应。&lt;/p&gt;&lt;p&gt;&lt;img alt=&quot;&quot; src=&quot;/public/upload/remote/2018/05/31/5b0f490b752c5.png&quot;/&gt;&lt;/p&gt;&lt;p&gt;　　“在过去的一年中，麦咖啡凭借一系列令人振奋的变革打破了新局面，而这一切只是刚刚开始。”麦咖啡菜单创新高级总监Elina Veksler在一份声明中表示， “我们的海龟咖啡饮料系列，代表的是一种放纵的享受。”的确，巧克力、焦糖、咖啡、榛子加一块的热量少不了。&lt;/p&gt;&lt;p&gt;　　实际上，这是麦咖啡2016年年末升级计划中提出推季节性产品的延续。&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;', '1527728387', '1527728387');
INSERT INTO `ey_article_content` VALUES ('41', '66', '&lt;p&gt;去年，咖门曾经写过一篇文章来专门讲述咖啡师的生存现状，薪资低、晋升难、出路窄成为咖啡师普遍认同的生存困境。&lt;/p&gt;&lt;p&gt;　　一些咖啡师对未来很迷茫，对咖啡的热爱难以放下，但是工作上的有限发展空间又让人为生计发愁。作为一名咖啡师，我们该如何规划未来职业发展呢，还是听一听过来人的建议吧!&lt;/p&gt;&lt;p&gt;&lt;img alt=&quot;&quot; src=&quot;/public/upload/remote/2018/05/31/5b0f4936d6d33.png&quot;/&gt;&lt;/p&gt;&lt;p&gt;　　一、服务员收入碾压咖啡师，要转行吗?&lt;/p&gt;&lt;p&gt;　　入行多年，咖啡师之路怨声载道，为何我们从萃取学到拉花，从拉花学到服务，外加英语口语无障碍，结果拿到的薪资有时还不如一家高级餐馆的服务生。&lt;/p&gt;&lt;p&gt;　　更讽刺的是，人家餐厅大多还包吃住。&lt;/p&gt;&lt;p&gt;　　况且一入咖啡深似海，从业至今各种买买买败败败，败粉锤、败奶缸、败器具、败豆子、败各种新鲜玩意并乐此不疲。&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;', '1527728412', '1527728412');
INSERT INTO `ey_article_content` VALUES ('42', '67', '&lt;p&gt;在中国，有句古话，“塞翁失马，焉知非福”。在澳洲，一名华裔程序员因为一个错误被公司开除，没想到的是，这一件事却促成他开启喜爱的咖啡事业。&lt;/p&gt;&lt;p&gt;&lt;img alt=&quot;&quot; src=&quot;/public/upload/remote/2018/05/31/5b0f494fcc529.png&quot;/&gt;&lt;/p&gt;&lt;p&gt;　　资料图：咖啡。李南轩 摄&lt;/p&gt;&lt;p&gt;　　来自堪培拉的36岁的梁奇云(音译，Calvin Leung)原本是一名IT程序员。自称“电脑书呆子”的他在当地一间企业工作5年后，在2008年因一次“小错误”被解雇。获得同事支持的梁奇云曾打算通过法律途径讨回公道，但他意识到自己其实不太喜欢这个工作。&lt;/p&gt;&lt;p&gt;　　“我在堪培拉长大，维持着一种生活现状：找工作、每天努力工作、挣一份还可以的工资，直到我发现我并不享受这种生活……”因此，他决定不再回到IT行业。转行的决定被亲友认为“疯狂”，但他对因祸得福感到非常高兴。&lt;/p&gt;&lt;p&gt;　　于是，梁在Dickson区买下一间咖啡店。在学习了基本运作后，他想改变从海外引入咖啡豆的模式——改用本地生产和烘干的新鲜咖啡豆。他在墨尔本找供货商，升级了咖啡机并重新培训了员工。几个月后，咖啡店的营业额增加了一倍多。&lt;/p&gt;&lt;p&gt;　　梁奇云的成功由此开始：他卖掉旧店，开了自己的特色咖啡店;又在2012年搬到澳洲咖啡市场的中心墨尔本，买下市区的Plantation咖啡店，然后迅速扩张，目前在墨尔本市区有5间店，在其它地区也有分店。&lt;/p&gt;&lt;p&gt;　　他的团队从最初的5人增加到40人，咖啡销售从第一年的7.5万杯到现在的50万杯，增幅高达500%。今年其销售额预计将达到200万澳元，远超第一年的40万澳元。&lt;/p&gt;&lt;p&gt;　　在梁奇云看来，事业转折点就是与大型百货连锁店的合作，这之后，其咖啡事业就获得巨大发展。而分析内部原因，公司的成功离不开统一的管理，从销售点到员工事物，每个环节都有统一的一套系统，这保证了公司有序进行。&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;', '1527728456', '1527728456');
INSERT INTO `ey_article_content` VALUES ('43', '68', '&lt;p&gt;近期，现磨&lt;a href=&quot;http://www.kafeipp.com/&quot; target=&quot;_blank&quot;&gt;咖啡&lt;/a&gt;品牌Coffee now获得A轮融资6000万元。此次的投资方包括深圳联合创投、深圳拓普理德一号投资基金、苏州星火众达产业投资基金、香港尚融集团总裁万洪春先生。据了解，本轮投资将用于国际级研发实验室的搭建及的咖啡生产基地无锡140亩工业园的搭建。值得思考的是，如此多&lt;a href=&quot;http://www.kafeipp.com/&quot; target=&quot;_blank&quot;&gt;咖啡品牌&lt;/a&gt;，为何Coffee now会受到众多资本的青睐呢?&lt;/p&gt;&lt;p&gt;　　Coffee now是深圳鼎加弘思饮品科技有限公司旗下咖啡品牌，主要生产现磨咖啡单杯饮品以及单杯饮品机。所谓单杯咖啡，也叫作胶囊咖啡，是将咖啡豆烘焙、养豆、研磨后氮气封装在耐高温、耐高压的“杯体”中，杯体使用高分子阻隔材料达到保鲜作用，摒弃了传统的防腐剂、添加剂，消费者可通过单杯饮品机完成标准的咖啡制作。&lt;/p&gt;&lt;p&gt;&lt;img alt=&quot;&quot; src=&quot;/public/upload/remote/2018/05/31/5b0f49669e5ee.png&quot;/&gt;&lt;/p&gt;&lt;p&gt;　　Coffee now通过在全球范围甄选优质咖啡豆及原料，以单杯的包装形式实现咖啡成品工业化量产，解决了咖啡原料运输，储存，保鲜，制作设备昂贵，操作复杂等一系列影响咖啡出品品质的相关问题，让消费者能以不到连锁咖啡一半的价格喝到高品质的优质咖啡。&lt;/p&gt;&lt;p&gt;　　Coffee now已经取得了100多项发明专利，其智能单杯饮品已打破技术壁垒，填补了国内市场空白。据卢应志介绍，Coffee now的核心技术其实是高分子阻隔材料在饮品运输、储存、制作过程中的工业化应用，与普通包装材料不同的是，Coffee now杯体材质采用了7层以上的高分子复合阻隔材料，具有极好的阻氧、阻水功能，避免光线穿透等外部因素对风味的影响。这样的包装技术让内部咖啡无需加入任何防腐剂、添加剂，还原天然提取原料的原有风味，从而保证了每一杯高品质的出品。&lt;/p&gt;&lt;p&gt;　　除了加工制造的业务，Coffee now还研发了与之配套的咖啡机，该咖啡机已经定型量产，根据应用场景和功能不同，价格从几百元到上万元不等。自主研发的具有胶囊自动识别功能的设备及各种自助设备也将投放市场。&lt;/p&gt;&lt;p&gt;　　Coffee now计划将咖啡机配合小型自动售货机推向办公室，让原本就有喝咖啡习惯的白领以单杯不足10元的价格，品尝到高品质的现磨咖啡。公司计划今年在办公室场景铺设10万台设备。&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;', '1527728478', '1527728478');
INSERT INTO `ey_article_content` VALUES ('44', '69', '&lt;p&gt;3月14日，霍金去世的当天，被尊称为日本“咖啡之神”的关口一郎去世，享年104岁。如果说，霍金是为星空而生的，那么，关口一郎就是为咖啡而生的。他一生都在研究咖啡，致力于为大家带来更纯正的咖啡风味。&lt;/p&gt;&lt;p&gt;　　万物皆有理论，唯有爱情无解&lt;/p&gt;&lt;p&gt;　　这是霍金说的，用来解释关口一郎的一生却很恰当，关口一郎一生把咖啡当成了他的爱人，终生未娶，和咖啡谈了一辈子恋爱，被尊称为“咖啡之神”。&lt;/p&gt;&lt;p&gt;&lt;img alt=&quot;&quot; src=&quot;/public/upload/remote/2018/05/31/5b0f497811a74.png&quot;/&gt;&lt;/p&gt;&lt;p&gt;　　▲想带给世界一杯真正的好咖啡&lt;/p&gt;&lt;p&gt;　　关口一郎的目标很简单，就是把咖啡整个明白。他认为好的咖啡，是需要日复一日的坚持才能达成的，他的一生就像一杯用68年时间打磨的琥珀咖啡一样，直达灵魂永垂不朽。&lt;/p&gt;&lt;p&gt;　　为了保持豆子的新鲜程度，他每次只烘焙小批量的豆子，半个多世纪以来，一直保持着每天三次烘焙的习惯，一生烘焙的豆子上百吨，但他仍然觉得，自己只是一直在接近，却还没能做出咖啡最纯粹的味道。&lt;/p&gt;&lt;p&gt;　　如果真的有平行宇宙存在，在那个空间里，我们希望他做出了自己心满意足的味道，请霍金喝一杯。&lt;/p&gt;&lt;p&gt;　　2&lt;/p&gt;&lt;p&gt;　　琥珀里时间的味道&lt;/p&gt;&lt;p&gt;　　在东京银座，从银座线新桥站出来步行大约5分钟，就到了一家名叫Cafe deL’Ambre的咖啡馆。&lt;/p&gt;&lt;p&gt;　　门脸不大，甚至不太起眼，只有铭牌上的“Coffee Only”(只有咖啡)透露出它的独特和坚持。&lt;/p&gt;&lt;p&gt;　　半个多世纪前，关口先生把咖啡厅开在这里，取名琥珀，并开出了银座咖啡最高单价。意外的是，这家咖啡厅不仅存活下来了，而且一开就是68年。&lt;/p&gt;&lt;p&gt;　　仅仅凭借一杯咖啡，在日本美食届的“大众点评”上，Cafe De L’ambre 一直稳稳地冠着“日本5千家顶级餐厅”的金牌。&lt;/p&gt;&lt;p&gt;　　Cafe deL’Ambre的招牌咖啡是“Old Beans”(老豆咖啡)，这是用存放了10年以上的咖啡豆烘制而成的。&lt;/p&gt;&lt;p&gt;　　发现这个“时间的秘密”，也是因为关口一郎在店里偶然发现一袋放置了许多年的豆子，一试竟然浓香扑鼻。&lt;/p&gt;&lt;p&gt;　　所以在Cafe deL’Ambre的菜单上，你能选择来自不同年份的豆子烘培出的咖啡，最老的可以追溯到1954年。&lt;/p&gt;&lt;p&gt;　　有人说，时代的浪潮让人无所适从，我们却从他的咖啡里喝出了坚守和情怀，那是时间的味道。&lt;/p&gt;&lt;p&gt;　　2018年3月14日先生往生，享年104岁，他终生未娶，无儿无女，用一生的时间探索咖啡的本质。&lt;/p&gt;&lt;p&gt;　　把一生献给咖啡事业的他，用一颗真挚的心去对待咖啡，因此换来醇香的味道。愿老人家在天国仍能与心爱的咖啡相伴。&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;', '1527728495', '1527728495');
INSERT INTO `ey_article_content` VALUES ('45', '70', '&lt;p&gt;在咖啡创业中，大家的生存现状如何呢?“6赔3平1赚。”这一句话足以概括。2017年，国内咖啡店的关店数量达到6万家，呈现负增长。在行业外的人，咖啡是白月光，浪漫、优雅、高端，而对行业内的人来说，运营咖啡馆是一项极具挑战性的事情。&lt;/p&gt;&lt;p&gt;　　今天的推文，来自一个有开店经历的店主，他用这些年踩过的坑，告诉你咖啡馆难赚钱的6个真相。&lt;/p&gt;&lt;p&gt;　　很多想开咖啡馆的人找到我，通常是看到一个他认为好的物业(地方)不知道做什么，然后向我询问一些细节，于是会产生这样的对话——&lt;/p&gt;&lt;p&gt;　　我：“开咖啡馆招到好员工很难。”&lt;/p&gt;&lt;p&gt;　　他们：“有什么难的，我有很多个XXX朋友，都能当靠谱的店长。”&lt;/p&gt;&lt;p&gt;　　我：“这个地方做推广会有难度。”&lt;/p&gt;&lt;p&gt;　　他们：“不会的，我朋友多，到时候每个人来一次就可以了。”&lt;/p&gt;&lt;p&gt;　　然后他还会列举他和那些朋友常常去哪家店，服务特不好，好像他们做这件事，就是为了提升行业标准。&lt;/p&gt;&lt;p&gt;　　遗憾的是，他们后来又会找我：“你身边有没有好的咖啡师啊?”“我想做个营销，你帮我写个文案呗。”&lt;/p&gt;&lt;p&gt;　　如果一个人打算做生意/创业，以赚钱为目的，不建议先选具体事情，而是先选对一个行业。&lt;/p&gt;&lt;p&gt;　　我问很多生意人的第一桶金怎么赚到的时候，他们的回答通常是：”我也不知道怎么回事，突然就赚到了这些钱。”这不是他们谦虚，事实的确如此，说他们比同起步的人天资聪颖，我看未必。&lt;/p&gt;&lt;p&gt;　　咖啡厅如果算行业，起码一线城市3年以内，二线城市5年以内并不适合成为第一桶金的选择，尤其是新手。&lt;/p&gt;&lt;p&gt;　　咖啡店大多数不赚钱，但每个城市都有赚钱的咖啡店，至于究竟要不要开店，下面这几条你可以当作贴士，也可以当作“拦路虎”。&lt;/p&gt;&lt;p&gt;　　真相1：不得不承认，选址决定一切。&lt;/p&gt;&lt;p&gt;　　顾客为什么要去你的咖啡店?&lt;/p&gt;&lt;p&gt;　　这应该是一家店的卖点，但并不是任何东西都可以成为卖点。&lt;/p&gt;&lt;p&gt;　　有人说：我的咖啡店有咖啡，说出这种话的不在少数，我真的无力反驳。&lt;/p&gt;&lt;p&gt;　　有人说：我这附近没有咖啡店。那么为什么没有呢?是大家都没看到商机还是以前开过倒闭了?&lt;/p&gt;&lt;p&gt;　　有人说：我的店饮品种类繁多，服务周到，这不是任何店的基本要求吗?&lt;/p&gt;&lt;p&gt;&lt;img alt=&quot;&quot; src=&quot;/public/upload/remote/2018/05/31/5b0f499de4b1f.png&quot;/&gt;&lt;/p&gt;&lt;p&gt;　　▲我的店里有咖啡，这个理由竟让我无言以对&lt;/p&gt;&lt;p&gt;　　我很难说服这些人，见过这么多店，我看到赚钱的只有这一个理由。&lt;/p&gt;&lt;p&gt;　　“我的店交通方便，潜在客户/周围的人选这家店决策成本最低”&lt;/p&gt;&lt;p&gt;　　商业/服务行业，有句话说的好：位置(选址)决定一切。&lt;/p&gt;&lt;p&gt;　　不同的地段租金差距巨大，从经营上理解，今天你多付出来的这部分房租，是为客户每次来节省出来的路费加时间成本，你是在替客户考虑，而客户只在乎自己能不能花最少的钱满足自己的要求。&lt;/p&gt;&lt;p&gt;　　星巴克也宁愿在高地价的地方租3~7个店面，而不会在不方便的地方安插一个孤岛，他们想的清楚：有名如星巴克，也不会有人大老远的去体验。&lt;/p&gt;&lt;p&gt;　　基于这个观点，我认为一些偏僻的网红店，无论把噱头搞得多么足，持续下去都会是非常困难的。&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;', '1540343730', '1540343730');
INSERT INTO `ey_article_content` VALUES ('35', '60', '&lt;p&gt;在现阶段消费升级的大背景下，更加追求咖啡品质又不忘性价比的80、90后成为消费主力。这在一定程度上促进了咖啡消费业态的转变：从咖啡馆里的消费诉求，逐渐变成大众饮品，即咖啡是现磨的，但价格是便宜的。消费群体的消费能力和消费偏好一旦发生变化，必然催生更丰富的咖啡业态，进而带动整个咖啡市场发生变化。&lt;/p&gt;&lt;p&gt;　　前不久，无人咖啡机服务运营商“友饮咖啡”获得由华创资本领投的亿元级A轮融资，以及全球顶尖新零售产业基金供应链的金融资金支持。资本加持后的无人咖啡机究竟是门怎样的生意?&lt;/p&gt;&lt;p&gt;　　隐形的咖啡作坊&lt;/p&gt;&lt;p&gt;　　随着人工成本的增高，线下咖啡店的毛利也在随之减少，加之咖啡店在选址及资金流的要求较高。而无人咖啡机刚好可以利用小面积的闲置空间触及C端用户。“咖啡是大的销售单品，既然消费者能接受半个小时的外卖点单，就更能接受在无人机前花一分钟打一杯现磨咖啡。”友饮CEO张阳解释道。&lt;/p&gt;&lt;p&gt;　　不同于欧美国家，现磨咖啡在中国市场还属于小众范畴，现磨咖啡与非现磨咖啡的占比为1：4，但在中国各类饮料产品中，现磨咖啡类饮品的增速仍高于其他品类。&lt;/p&gt;&lt;p&gt;　　虽然无人咖啡机想要切入现磨咖啡领域，但对其而言，早期研发阶段，首先在口味上并不能无限接近人工冲泡的口感;其次是卫生，无人咖啡机是个大型的密闭空间，糖、奶、咖啡残留物都是滋生细菌的温床，在这块看不见的领域，该如何保证产品质量?&lt;/p&gt;&lt;p&gt;　　张阳认为，要想做好一杯咖啡，就要把业态跑通。而咖啡所讲究的干度、纯度、苦度、香气、酸度等多个因素都会影响咖啡的品质，就既需要好的技术和设备，以及好的食材和运营。&lt;/p&gt;&lt;p&gt;　　为了降低故障率，目前新一代的友饮摒弃了原先内置的管路技术，而是升级为机械臂，通过机械手来进行物料的切换，避免了奶糖残留造成的管路堵塞，进而实现时间、用料、用量上的精准操作。比如每台机器里的两个机械臂，一个拿着杯去接奶、咖啡和糖，另一个则用空气搅拌技术使之充分融合。&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;', '1527673103', '1527673103');
INSERT INTO `ey_article_content` VALUES ('36', '61', '&lt;p&gt;　一边是教育和培育了中国咖啡市场的全球咖啡连锁巨头——星巴克，一边是自带互联网基因的咖啡界新秀——瑞幸咖啡(Luckin coffee)，它们的相遇注定激情四射。&lt;/p&gt;&lt;p&gt;　　近日，瑞幸咖啡(Luckin coffee)在北京召开新闻发布会，并同时发出一封言辞激烈的公开信，指责星巴克“涉嫌垄断”。在成立不到1年的时间里，瑞幸便单方面走到了和星巴克“正面交锋”的地步。对此，星巴克淡定表示：“无意参与其他品牌的市场炒作”。&lt;/p&gt;&lt;p&gt;　　真实情况如何呢?是瑞幸被逼无奈出此对策，还是一场品牌炒作的秀?&lt;/p&gt;&lt;p&gt;　　“涉嫌垄断”&lt;/p&gt;&lt;p&gt;　　我们首先回顾一下瑞幸咖啡这封信(如下)的要点。&lt;/p&gt;&lt;p&gt;　　在这封致星巴克的一封公开信中，瑞幸咖啡称星巴克与很多物业签订的合同中存在排他性条款，此外星巴克对瑞幸咖啡的供应商伙伴“频繁施压要求站队”。由此，该公司认为星巴克涉嫌违反了《反垄断法》，并声言委托律所提起诉讼。&lt;/p&gt;&lt;p&gt;　　瑞幸咖啡在给笔者发来的另一封关于律师对采取本次法律行动的简要介绍的文件中引述欧睿国际的数据称，星巴克在中国咖啡馆服务的市场中，去年的份额为58.6%。&lt;/p&gt;&lt;p&gt;　　“确凿的证据表明，星巴克利用自己的先占优势和市场支配地位......采用自己预先拟订好的格式合同，严辞约定排他条款。”上述文件说，该公司拟向有关法院提起民事诉讼。&lt;/p&gt;&lt;p&gt;　　不过，这份文件并未同时透露它计划采取以上法律行动的时间，以及选择哪些法院提起诉讼。&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;', '1527673130', '1527673130');
INSERT INTO `ey_article_content` VALUES ('37', '62', '&lt;p&gt;说起中国咖啡市场的建立，星巴克功不可没。&lt;/p&gt;&lt;p&gt;　　星巴克花了整整17年时间，从一线城市到三四线城市，由点到面逐步渗透，截至2017年底，星巴克在中国大陆门店数量已超3000家，并且扩张步伐还在加快。&lt;/p&gt;&lt;p&gt;　　面对这个被星巴克教育和培养出来的咖啡市场，众多资本和品牌垂涎三尺，最终揭竿而起。便利店咖啡、精品咖啡、互联网咖啡……不断在抢占和餐食咖啡市场。那么，到底谁会成为赢家、笑到最后呢?&lt;/p&gt;&lt;p&gt;　　便利店咖啡：底层大佬&lt;/p&gt;&lt;p&gt;　　星巴克要在中国继续扩张，那品牌下沉是绕不开的，第三空间虽好，但架不住他贵啊。&lt;/p&gt;&lt;p&gt;　　在一二线城市，均价30一杯的星巴克对消费升级人群来说也许不算什么，但对消费相对保守的三四线城市来说，这价格就高了。&lt;/p&gt;&lt;p&gt;　　相比起来，依托7-11、全家、罗森等便利店生存的快餐咖啡在这些地区反而能混得风生水起，再加上便利店内消费链完整，消费者在这里得到的不仅仅是一杯咖啡，还有配咖啡的蛋糕、便当等。&lt;/p&gt;&lt;p&gt;　　与此同时，星巴克的老对头McCafe最近也有新动作，依托麦当劳，麦咖啡正以惊人的速度在全球范围内扩张，10元一杯的价格，更加亲民，也更加便捷。&lt;/p&gt;&lt;p&gt;　　曾十分挚爱咖啡的谢女士调侃称，“我现在消费‘降级’了，改喝普洱茶。”“喝星巴克咖啡一天大30元，喝不起。10元/杯的麦当劳咖啡也不错。”&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;', '1527673153', '1527673153');
INSERT INTO `ey_article_content` VALUES ('34', '59', '&lt;p&gt;最近咖啡界炸了：星巴克被告了……&lt;/p&gt;&lt;p&gt;　　状告星巴克的是新晋网红咖啡品牌——瑞幸咖啡。瑞幸这条“鲶鱼”已经玩的风生水起，将中国的咖啡市场搅的天翻地覆了。　　5月8号，瑞幸咖啡在国家会议中心召开新闻发布会，宣布完成500家 门店布局，试营业结束，正式营业开始。数据显示，截止5月8号，4个月的试运营期间，瑞幸咖啡为130万用户 供应了500万杯咖啡 。而在华开业12年的costa咖啡仅仅只有420家门店。&lt;/p&gt;&lt;p&gt;&lt;/p&gt;&lt;p&gt;　　作为普通的餐饮从业者 ，从瑞幸咖啡的疯狂扩张中 我们能学到什么?&lt;/p&gt;&lt;p&gt;　　互联网营销也是碰瓷的艺术&lt;/p&gt;&lt;p&gt;　　开业营销最重要是获取足够多的关注。越快的时间让越多的人知道就是省钱。瑞幸咖啡当然明白这一点。&lt;/p&gt;&lt;p&gt;　　今年3月份，笔者就在朋友圈 看到了瑞幸咖啡的广告，并且一个月内看到了3次。再后来，电梯 里铺天盖地的都是汤唯和张震的“这一杯，谁不爱”海报。&lt;/p&gt;&lt;p&gt;　　第一步：“干掉星巴克”&lt;/p&gt;&lt;p&gt;　　瑞幸一登场的定位就是干掉星巴克。瑞幸咖啡创始人钱治亚公开表示，“超越星巴克， 是我们创业的目标”。&lt;/p&gt;&lt;p&gt;　　比星巴克低10块的中段价格定位，大师拼配的阿拉卡比咖啡豆，30分钟内不到就赔的顺丰配送。全部与星巴克差异化。 瑞幸咖啡努力给大家树立一个形象，“我是人人都喝得起、喝的到的好咖啡哦!”&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;', '1527673068', '1527673068');
INSERT INTO `ey_article_content` VALUES ('14', '39', '&lt;p&gt;随着沙拉的接受度越来越高、品质门槛不断提升，花盛咖啡不断增加沙拉种类和配料，牛油果、芝士碎、培根、黍米、橄榄、香料、香草等高级材料搭配成口感丰富的沙拉轻食，成为吸引年轻人消费的明星产品。&lt;/p&gt;&lt;p&gt;&lt;img src=&quot;/public/upload/article/2018/05/30/f54636e4c7abbd45880e412f58041193.jpg&quot; title=&quot;f54636e4c7abbd45880e412f58041193.jpg&quot; alt=&quot;f54636e4c7abbd45880e412f58041193.jpg&quot;/&gt;&lt;/p&gt;&lt;p&gt;有业内人士指出，传统的咖啡厅店大、空间幽暗，大多设置包厢，给人厚重感和私密感，而现在受年轻人喜爱的咖餐厅，则更看重社交空间的打造。另外，不单靠情怀和品牌故事，而更靠标准化管理来切切实实地盈利，花盛咖啡率先把务实放在了首位。&lt;/p&gt;', '1527667468', '1527667468');
INSERT INTO `ey_article_content` VALUES ('15', '40', '&lt;p&gt;喝咖啡、吃西餐这已经成为了愈来愈多的现代都市男女们的一种新时尚。周末闲暇的时候，约上几个好友，或者是就自己一个人，捧一本书去自己喜欢的咖啡馆里，挑选一个靠窗的角落，再点上一份美味的咖啡，细细品茗，无疑是人生的一大乐事了。&lt;/p&gt;&lt;p&gt;&lt;img src=&quot;/public/upload/article/2018/05/30/5f9014335f8888bd05486f42e6bbef70.jpg&quot; title=&quot;5f9014335f8888bd05486f42e6bbef70.jpg&quot; alt=&quot;5f9014335f8888bd05486f42e6bbef70.jpg&quot;/&gt;&lt;/p&gt;&lt;p&gt;现在，伴随着去咖啡馆里享受悠闲慢时光的人愈来愈多，咖啡馆的生意也是非常的火爆，这样也就造就了一家又一家咖啡馆的相继开业。许多的饮食男女也开始由喜欢喝咖啡而想要拥有一家自己的咖啡馆，这样就能一边舒适的享受生活，一边还解决了生计问题。&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;', '1527667811', '1527667811');
INSERT INTO `ey_article_content` VALUES ('16', '41', '&lt;p&gt;现在，伴随着去咖啡馆里享受悠闲慢时光的人愈来愈多，咖啡馆的生意也是非常的火爆，这样也就造就了一家又一家咖啡馆的相继开业。许多的饮食男女也开始由喜欢喝咖啡而想要拥有一家自己的咖啡馆，这样就能一边舒适的享受生活，一边还解决了生计问题。&lt;/p&gt;&lt;p&gt;&lt;img src=&quot;/public/upload/article/2018/05/30/654d4810d9882daaddabb260b30ff0b1.jpg&quot; title=&quot;654d4810d9882daaddabb260b30ff0b1.jpg&quot; alt=&quot;654d4810d9882daaddabb260b30ff0b1.jpg&quot;/&gt;&lt;/p&gt;&lt;p&gt;可是，许多人想要开咖啡馆，却担心自己经验不足，最后在犹犹豫豫之后也把自己开咖啡馆创业的想法抛到了九霄云外之外。有梦想就要大胆去追，而不应该被生活的琐事羁绊了双腿。如果您想要拥有一家自己的咖啡店，只要您做好了充分的准备，那花盛咖啡就送您一家时尚别致的咖啡馆，手把手教您经营、专业设计团队为您出谋划策，让您轻松经营，坐收财富。&lt;/p&gt;&lt;p&gt;没有经验，担心失败，这是横在许多有创业想法却迟迟没有行动的年轻人面前的两座大山。其实，没有经验并不可怕，可怕的是没有实际的行动。难道因为害怕摔倒就不奔跑了吗？加盟花盛咖啡，这些都将不再是阻挡追求梦想的借口。因为花盛咖啡加盟总部设有合作商培训中心，会为每一个加盟商提供系统培训课程，包括理论知识、运营管理、产品制作、现场实操，然后在去公司的门店实习，等到毕业考核通过了之后，学员也就全面掌控了咖啡馆的经营技能。&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;', '1527667883', '1527667883');
INSERT INTO `ey_article_content` VALUES ('17', '42', '&lt;p&gt;这是咖啡最坏的时代，也是咖啡最好的时代。在体验消费时代，在客群年轻化迭代的今天，从未有一个新的品类，如此受到年轻人的青睐。它是一个符号，是一种文化，更是一个充满朝气的新兴行业，其消费市场年增长率高达16%，高于世界平均水准，所以咖啡创业时代到来了！&lt;/p&gt;&lt;p&gt;&lt;img src=&quot;/public/upload/article/2018/05/30/591a3368b0b55cb6aa468a9f86a8a570.jpg&quot; title=&quot;591a3368b0b55cb6aa468a9f86a8a570.jpg&quot; alt=&quot;591a3368b0b55cb6aa468a9f86a8a570.jpg&quot;/&gt;&lt;/p&gt;&lt;p&gt;咖啡，年轻一代的娱乐就餐新选择！以其“大道至简，极简致奢”的装修理念，打造出多样化的花盛咖啡西餐厅。花盛咖啡，以年轻一代生活基调为导向，集合了娱乐，社交，环境、场景、设计于一体，打造出的咖啡音乐西餐品牌，创立的初衷来源于新一代的消费群体所传递出来的对生活的热爱，对社交的追求以及对文化的感知。花盛咖啡提供的是一种环境，为80、90、95后提供一个可以放飞自我，释放自己的场所。用更多的元素去打造一个更加活跃的氛围，让花盛咖啡经营的不仅仅是一间咖啡厅，更是经营的一种空间，一种年轻的活力。秉承“别让外物设限，你就是你自己”的初心，在不断的发展汇集中，倡导“释放自己，回归初心”的全新生活状态。&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;', '1527671760', '1527671760');
INSERT INTO `ey_article_content` VALUES ('18', '43', '&lt;p&gt;咖啡免费提供的服务：&lt;/p&gt;&lt;p&gt;1.人员支持，总部派相关的前厅经理，厨师团队，运营团队辅助开业并长期驻店经营，总部后期持续不断督导，新品开发营销服务。&lt;/p&gt;&lt;p&gt;2.品牌授权，免费提供全套花盛视觉VI形象系统及品牌授权证书，商标使用证书&lt;/p&gt;&lt;p&gt;3.开业相关用品，免费提供全套花盛咖啡培训材料&lt;/p&gt;&lt;p&gt;4.店铺全套设计，免费提供店铺设计方案（店面平面设计、店内、店外、大厅等相关效果图）&lt;/p&gt;&lt;p&gt;5.专业设备器具、餐具、原材料，免费赠送开店专用的咖啡设备、餐具、器具。免费赠送开店原料和辅料，以及音乐光盘和厨师服装。&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;&lt;a&gt;&lt;img src=&quot;/public/upload/article/2018/05/30/ac9528f98649928f127f5ee5c886301e.jpg&quot; title=&quot;ac9528f98649928f127f5ee5c886301e.jpg&quot; alt=&quot;ac9528f98649928f127f5ee5c886301e.jpg&quot;/&gt;&lt;/a&gt;&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;', '1527671853', '1527672155');
INSERT INTO `ey_article_content` VALUES ('19', '44', '&lt;p&gt;喝咖啡、吃西餐这已经成为了愈来愈多的现代都市男女们的一种新时尚。周末闲暇的时候，约上几个好友，或者是就自己一个人，捧一本书去自己喜欢的咖啡馆里，挑选一个靠窗的角落，再点上一份美味的咖啡，细细品茗，无疑是人生的一大乐事了。&lt;/p&gt;&lt;p&gt;现在，伴随着去咖啡馆里享受悠闲慢时光的人愈来愈多，咖啡馆的生意也是非常的火爆，这样也就造就了一家又一家咖啡馆的相继开业。许多的饮食男女也开始由喜欢喝咖啡而想要拥有一家自己的咖啡馆，这样就能一边舒适的享受生活，一边还解决了生计问题。&lt;/p&gt;&lt;p&gt;&lt;img src=&quot;/public/upload/article/2018/05/30/ac6dd90882b73e0e8542d572a363a28f.jpg&quot; title=&quot;ac6dd90882b73e0e8542d572a363a28f.jpg&quot; alt=&quot;ac6dd90882b73e0e8542d572a363a28f.jpg&quot;/&gt;&lt;/p&gt;&lt;p&gt;可是，许多人想要开咖啡馆，却担心自己经验不足，最后在犹犹豫豫之后也把自己开咖啡馆创业的想法抛到了九霄云外之外。有梦想就要大胆去追，而不应该被生活的琐事羁绊了双腿。如果您想要拥有一家自己的咖啡店，只要您做好了充分的准备，那花盛咖啡就送您一家时尚别致的咖啡馆，手把手教您经营、专业设计团队为您出谋划策，让您轻松经营，坐收财富。&lt;/p&gt;&lt;p&gt;没有经验，担心失败，这是横在许多有创业想法却迟迟没有行动的年轻人面前的两座大山。其实，没有经验并不可怕，可怕的是没有实际的行动。难道因为害怕摔倒就不奔跑了吗？加盟花盛咖啡，这些都将不再是阻挡追求梦想的借口。因为咖啡加盟总部设有合作商培训中心，会为每一个加盟商提供系统培训课程，包括理论知识、运营管理、产品制作、现场实操，然后在去公司的门店实习，等到毕业考核通过了之后，学员也就全面掌控了咖啡馆的经营技能。&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;', '1527671916', '1527672136');
INSERT INTO `ey_article_content` VALUES ('20', '45', '&lt;p style=&quot;text-align:center&quot;&gt;&lt;img src=&quot;/public/upload/article/2018/05/30/380439e671dec014ba15cd64d47ecf92.jpg&quot; title=&quot;380439e671dec014ba15cd64d47ecf92.jpg&quot; alt=&quot;380439e671dec014ba15cd64d47ecf92.jpg&quot;/&gt;&lt;/p&gt;&lt;p&gt;现在的咖啡馆是一个比一个装修有逼格，一个比一个有格调。这一点大家更不用担心，因为花盛咖啡致力打造当下最流行、高逼格的咖啡馆风格，合作店装修期间，总部设计师会提供全方位设计，结合店铺形态规划品牌独特的复合文化观感体验，并制定详细的装修标准与流程，同时也可满足加盟商对店面装修要求的创意体现。&lt;/p&gt;&lt;p&gt;在新店开业期间，总部还会安排驻店指导，为您全方位策划新店开业活动，确保新店一炮而红。而且在新店开业之后，总部还会持续推新，根据您店铺的实际情况因地制宜的制定节日营销策划。总部凭借强大研发能力及高端策划水平，在季度及节日中持续推出新品，配合全媒体策略营销推广，给予门店源源不竭的新鲜生命力。&lt;/p&gt;&lt;p&gt;在咖啡馆步入正轨之后，咖啡总部依然还是会提供帮助和支持，为了更好的打造品牌，提升品牌核心竞争力，总部每年还会持续不断的在全国性媒体、企业网站、门户网站、微信微博、行业展会进行品牌广告宣传，全面开拓市场，持续建设优质门店，提升品牌知名度，为加盟商带来品牌所产生的经济利益。&lt;/p&gt;&lt;p&gt;是不是已经心动了？那就不要犹豫，机会只是留给有准备的人。听说花盛咖啡加盟名额有限哦，手慢无!&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;', '1527672247', '1527672247');
INSERT INTO `ey_article_content` VALUES ('21', '46', '&lt;p&gt;鲜花盛开之时，正是一生最美之时；花盛咖啡，以年轻一代生活基调为导向，集合了娱乐，社交，环境、场景于一体，打造出的西餐音乐酒吧品牌，创立的初衷来源于新一代的消费群体所传递出来的对生活的热爱，对社交的追求以及对文化的感知。花盛咖啡提供的是一个场所，为80、90后提供一个可以放飞自我，释放自己的场所。花盛咖啡，用更多的元素去打造一个更加活跃的氛围，让花盛咖啡经营的不仅仅是一间咖啡厅，更是经营的一种空间，一种年轻的活力。秉承“别让外物设限，你就是你自己”的初心，在不断的发展汇集中，倡导“释放自己，回归初心”的全新生活状态。花盛咖啡致力于为80、90后年轻消费群体打造活力与时代的闲逸生活圈，以咖啡味源点，为用户连接每一种生活体验。在这里我们为用户提供极具现代感的场所，时尚生活的格调品味，在音乐与酒与咖啡的融合中真正的释放自己，找到最初的生命体。为用户、为伙伴创造更多的正能量价值，引领年轻态的生活方式。&lt;/p&gt;&lt;p style=&quot;text-align:center&quot;&gt;&lt;img src=&quot;/public/upload/article/2018/05/30/fff8501af83dd79bf0b7bdf169940f56.jpg&quot; title=&quot;fff8501af83dd79bf0b7bdf169940f56.jpg&quot; alt=&quot;fff8501af83dd79bf0b7bdf169940f56.jpg&quot;/&gt;&lt;/p&gt;&lt;p&gt;咖啡发展区域区域集中在北京、深圳、上海等一线城市，目前主要拓展区域为华东、华南、西南等地区，目前店面数量全国达到50余家店面。花盛咖啡，带着对生命本质的诉求，寻找最原始最纯真的初心，我们用心经营，精心打造这个属于年轻一代的王国。为在路上不断进发的人提供一个片刻释放的精神乐园，优雅的休息环境，赏心悦目的书籍陪伴，高品质的咖啡甜点，专业有素的服务人员让顾客的每次到来都能流连忘返。&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;', '1527672332', '1527672332');
INSERT INTO `ey_article_content` VALUES ('22', '47', '&lt;p&gt;1、投资咨询：投资者以电话、网上留言等方式向总部专业的投资顾问咨询相关合作事项。&lt;/p&gt;&lt;p&gt;2、实地考察：投资者到总部所在地进行项目考察，和总部交流。&lt;/p&gt;&lt;p&gt;3、店面评估：总部派专业的运营团队到当地进行市场调研与店面评估&lt;/p&gt;&lt;p&gt;4、签订合同：双方确认考察结果无争议，正式签订合同。&lt;/p&gt;&lt;p&gt;5、缴纳费用：需向总部缴纳费用。&lt;/p&gt;&lt;p&gt;6、总部培训：总部安排投资者进行技术培训。&lt;/p&gt;&lt;p&gt;7、店面装修：总部为加盟者提供装修指导。&lt;/p&gt;&lt;p&gt;8、开业：总部持续关注加盟者的经营情况，并给予经营指导与帮助。&lt;/p&gt;&lt;p&gt;&lt;img src=&quot;/public/upload/article/2018/05/30/c5d762d634992b8cd28b1523be720ecc.jpg&quot; title=&quot;c5d762d634992b8cd28b1523be720ecc.jpg&quot; alt=&quot;c5d762d634992b8cd28b1523be720ecc.jpg&quot;/&gt;&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;', '1527672391', '1527672391');
INSERT INTO `ey_article_content` VALUES ('23', '48', '&lt;p&gt;据了解，目前速溶咖啡仍主导着我国主要的咖啡市场，市场份额达71.8%，现磨与即饮咖啡市场份额分别为10.1%和18.1%。不过人们对于速溶咖啡的功能诉求大都只为提神，而非出自对咖啡的喜爱。随着人们生活理念的进一步提高，未来售价更高，口感更好的即饮与现磨咖啡将进一步获得市场青睐。&lt;/p&gt;&lt;p style=&quot;text-align:center&quot;&gt;&lt;img src=&quot;/public/upload/article/2018/05/30/0a7cda40e6bc0cc2e20ea9c4fb47d4dc.jpg&quot; title=&quot;0a7cda40e6bc0cc2e20ea9c4fb47d4dc.jpg&quot; alt=&quot;0a7cda40e6bc0cc2e20ea9c4fb47d4dc.jpg&quot;/&gt;&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;', '1527672441', '1527672441');
INSERT INTO `ey_article_content` VALUES ('24', '49', '&lt;p&gt;现在的年轻白领寻求质量与风格，甘愿花更多的钱去享用一种空气和文明，在高节奏的都市生活中巴望有一个安静舒适的环境给自己歇息，咖啡厅就是他们所寻求的地方。现在我国咖啡商场上已经有许多海内外品牌，例如：星巴克、咖啡等等，现在咖啡加盟店也是越来越多出资者瞄准的好市场。那么咖啡行业赢利高吗?咖啡行业远景怎么样?这里花盛咖啡将为我们剖析咖啡行业未来发展趋势。&lt;/p&gt;&lt;p&gt;&lt;img src=&quot;/public/upload/article/2018/05/30/2e40f7f5fb79efd629962dc438968b59.png&quot; title=&quot;2e40f7f5fb79efd629962dc438968b59.png&quot; alt=&quot;2e40f7f5fb79efd629962dc438968b59.png&quot;/&gt;&lt;/p&gt;&lt;p&gt;咖啡多年管理运营者对目前咖啡厅市场未来发展趋势进行剖析，同时咖啡将跟行业的步伐，打造真正符合市场发展的咖啡品牌！&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;', '1527672486', '1527672486');
INSERT INTO `ey_article_content` VALUES ('25', '50', '&lt;p&gt;咖啡培育&lt;/p&gt;&lt;p&gt;咖啡出产栽培将逐步集中。现如今咖啡店加盟商可谓是人山人海，咖啡加盟店也相同为那些喜欢喝咖啡的人供给了不少便利。近年来我国咖啡豆总产值持续增加，阐明咖啡豆的栽培技能有着显着的进步。跟着咖啡店加盟商部队的强大，国家对外资出资咖啡栽培持鼓舞的情绪，我国未来几年里咖啡豆的产值仍将坚持持续增加。“公司+基地+农户”将成为首要的咖啡栽培和加工方式。&lt;/p&gt;&lt;p&gt;营销方式&lt;/p&gt;&lt;p&gt;现在的大中城市，咖啡消费日趋老练，运营业态向小型、简约的大众化咖啡店方式开展，推出咖啡加盟餐厅、咖啡推车、咖啡加盟小巴等营销方式。针对不同消费集体定位，更多的咖啡的出售业态还会连续涌现。&lt;/p&gt;&lt;p&gt;&lt;img src=&quot;/public/upload/article/2018/05/30/4bb47cf48a45c94ee580a5a8588c4645.jpg&quot; title=&quot;4bb47cf48a45c94ee580a5a8588c4645.jpg&quot; alt=&quot;4bb47cf48a45c94ee580a5a8588c4645.jpg&quot;/&gt;&lt;/p&gt;&lt;p&gt;品牌效应&lt;/p&gt;&lt;p&gt;跟着咖啡职业的开展，咖啡店的竞赛也越来越大，因为咖啡消费人群一般都是中高档的消费集体，因而咖啡店品牌的知名度对其未来的开展尤为重要，咖啡店即将重视品牌运营的理念，咖啡运营者只有对未来的开展趋势有一个精确的掌握，才干防止和战胜日后运营中可能呈现的问题。&lt;/p&gt;&lt;p&gt;装修风格&lt;/p&gt;&lt;p&gt;咖啡店未来的成功，离不开现有好的地理位置和精美的装修。我们都知道人们进一家咖啡店，更大程度上是为了享用舒适的店内环境而来，那么咖啡店的装修将直接影响咖啡店生意的好坏。出资者需求针对的不同的商场人群和创业的层次来做咖啡加盟店。&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;', '1527672561', '1527672561');
INSERT INTO `ey_article_content` VALUES ('26', '51', '&lt;p&gt;荣誉证书，是记录获得的嘉奖荣誉，并把它作为奖励颁发给得奖人进行收藏的证明性文书，其有热合、缝线、裱糊等制作工艺，证书内芯分为普通内芯和定做内芯，外形各种各样，由使用单位自行决定采用哪种，且不同的单位使用的格式不同。热合，缝线工艺的证书一般用塑膜做套或斜角来夹放内芯，也有用光丝带做斜角的，裱糊工艺的证书一般用光丝带做斜角，也有用透明膜做成夹层，插页式放置内芯（内芯需定制）。任务分析：10名学生证书逐个编辑并打印，这是常规作法，我们可以word的高级功能邮件合并非常轻松很简单的就完成此项工作，并且数量越大，越能显出他的优越性。&lt;/p&gt;', '1527672783', '1527672845');
INSERT INTO `ey_article_content` VALUES ('27', '52', '&lt;p&gt;荣誉证书，是记录获得的嘉奖荣誉，并把它作为奖励颁发给得奖人进行收藏的证明性文书，其有热合、缝线、裱糊等制作工艺，证书内芯分为普通内芯和定做内芯，外形各种各样，由使用单位自行决定采用哪种，且不同的单位使用的格式不同。热合，缝线工艺的证书一般用塑膜做套或斜角来夹放内芯，也有用光丝带做斜角的，裱糊工艺的证书一般用光丝带做斜角，也有用透明膜做成夹层，插页式放置内芯（内芯需定制）。任务分析：10名学生证书逐个编辑并打印，这是常规作法，我们可以word的高级功能邮件合并非常轻松很简单的就完成此项工作，并且数量越大，越能显出他的优越性。&lt;/p&gt;', '1527672851', '1527672851');
INSERT INTO `ey_article_content` VALUES ('28', '53', '&lt;p&gt;荣誉证书，是记录获得的嘉奖荣誉，并把它作为奖励颁发给得奖人进行收藏的证明性文书，其有热合、缝线、裱糊等制作工艺，证书内芯分为普通内芯和定做内芯，外形各种各样，由使用单位自行决定采用哪种，且不同的单位使用的格式不同。热合，缝线工艺的证书一般用塑膜做套或斜角来夹放内芯，也有用光丝带做斜角的，裱糊工艺的证书一般用光丝带做斜角，也有用透明膜做成夹层，插页式放置内芯（内芯需定制）。任务分析：10名学生证书逐个编辑并打印，这是常规作法，我们可以word的高级功能邮件合并非常轻松很简单的就完成此项工作，并且数量越大，越能显出他的优越性。&lt;/p&gt;', '1527672879', '1527672879');
INSERT INTO `ey_article_content` VALUES ('29', '54', '&lt;p&gt;荣誉证书，是记录获得的嘉奖荣誉，并把它作为奖励颁发给得奖人进行收藏的证明性文书，其有热合、缝线、裱糊等制作工艺，证书内芯分为普通内芯和定做内芯，外形各种各样，由使用单位自行决定采用哪种，且不同的单位使用的格式不同。热合，缝线工艺的证书一般用塑膜做套或斜角来夹放内芯，也有用光丝带做斜角的，裱糊工艺的证书一般用光丝带做斜角，也有用透明膜做成夹层，插页式放置内芯（内芯需定制）。任务分析：10名学生证书逐个编辑并打印，这是常规作法，我们可以word的高级功能邮件合并非常轻松很简单的就完成此项工作，并且数量越大，越能显出他的优越性。&lt;/p&gt;', '1527672903', '1527672903');
INSERT INTO `ey_article_content` VALUES ('30', '55', '&lt;p&gt;　咖啡在中国起步比较晚，早期只是高消费人群或海归人士才有兴致到咖啡馆喝上一杯，而随着时间的推移，国内咖啡市场已经发生了翻天覆地的变化。中国作为世界咖啡市场的重要部分，备受外媒关注，下面我们就来看看外媒眼中的咖啡市场吧!&lt;/p&gt;&lt;center&gt;近期，外媒称，咖啡正在挤进以茶文化为主的中国市场，这里的咖啡消费量很有可能从全球垫底变成全球领先。中国的咖啡饮用量在全球的占比不足2%，但这个行业已经开始改变。&lt;br/&gt;&lt;/center&gt;&lt;p&gt;　　据英国广播公司网站7月14日报道，2005年，理查德·钱(Richard Chien)在中国东北开了一家咖啡厅。那时候，一群刚刚出徒的咖啡师每天大约能调制900杯咖啡，每杯的价格为6元——换算过来还不到1美元。10年后，他在北京经营一家高级咖啡学校。在那里，学员要花费好几个小时了解咖啡豆的芳香和品鉴技巧，培训过程中使用的咖啡每杯价格为6美元(约合40元人民币)。&lt;/p&gt;&lt;p&gt;　　中国的“经济已经发生了变化，人们越来越理解不同的生活方式。”理查德·钱说，“他们眼中不再只有茶。”&lt;/p&gt;&lt;p&gt;　　根据美国农业部的数据，中国的咖啡消费量过去4年增长近两倍，在其追踪的所有大型市场中位居首位。中国市场的潜力也无比巨大：该国总人口多达14亿。&lt;/p&gt;&lt;p&gt;　　星巴克对中国市场满怀信心，因此准备明年在上海开张第一家国际烘烤和体验中心。该公司相信，中国将成为其最大的市场。这家总部位于西雅图的咖啡连锁企业已经在中国拥有2000多家门店，他们还计划在未来5年每年新增500家店。另外一家美国咖啡连锁企业唐恩都乐去年宣布将在未来20年新增逾1400家门店，增幅接近100倍。&lt;/p&gt;&lt;p&gt;　　报道称，咖啡销量的增长证明中国正在向消费驱动型经济转型，这主要源自不断扩大的中产阶级消费意愿的变化。越来越多的中国人前往国外旅游，有的在日本品尝浓缩咖啡，有的在美国的咖啡厅内完成学期论文。与父辈们不同的是，很多中国年轻一代都是在咖啡厅的包围下长大的。&lt;/p&gt;&lt;p&gt;　　“它刚好位于随意性家庭开支的甜蜜点上。”北京大学投资学教授高子程说，他曾与人合著了《一小时读懂中国消费者：5个小故事了解10亿消费者的残酷斗争》一书。很多中国人看来，喝咖啡仍然像是一种嗜好，但这却是一种并不遥远的奢华生活方式。&lt;/p&gt;&lt;p&gt;　　在增速放缓的中国经济中，其他商品的需求都在不断降低，但咖啡却似乎是个异类。该国庞大的人口基数和几乎未经挖掘的市场都蕴含着前所未有的机遇。&lt;/p&gt;&lt;p&gt;　　根据市场研究公司欧瑞国际提供的数据，中国人平均每人每年饮用3杯咖啡，在全球排名几乎垫底，仅高于苏丹和朝鲜这样的国家。相比而言，美国人平均每人每年饮用363杯咖啡，英国人也达到250杯。&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;', '1527672957', '1527672957');
INSERT INTO `ey_article_content` VALUES ('31', '56', '&lt;p&gt;目前，中国已经成为咖啡消费最快的国家之一，消费年增长率在25%左右，市场规模也达到了2000亿元。从消费结构来看，速溶咖啡仍然占据国内主要市场，而即饮和现磨咖啡也逐渐受到市场的认可。　　&lt;/p&gt;&lt;p&gt;咖啡消费市场年增长25% 即饮与现磨将更受青睐&lt;/p&gt;&lt;p&gt;　　据了解，目前速溶咖啡仍主导着我国主要的咖啡市场，市场份额达71.8%，现磨与即饮咖啡市场份额分别为10.1%和18.1%。不过人们对于速溶咖啡的功能诉求大都只为提神，而非出自对咖啡的喜爱。随着人们生活理念的进一步提高，未来售价更高，口感更好的即饮与现磨咖啡将进一步获得市场青睐。&lt;/p&gt;&lt;p&gt;　　据市场数据表明，咖啡加盟行业正以每年25%速度增长，而咖啡正成为城市文明进步、经济增长的亮点。二三线城市消费量不断增加，所占的市场额也不断增加，将成为中国未来20年的核心推动市场。&lt;/p&gt;&lt;p&gt;　　中国是世界咖啡消费增长最快的国家之一。目前中国的咖啡消费金额为2000亿元人民币左右。消费以每年25%的速度增长，而世界的咖啡消费平均增长率为2.5%。&lt;/p&gt;&lt;p&gt;　　目前整个中国的咖啡消费仍处于起步阶段，增长速度惊人：年销量3万吨至4万吨之间，每年保持在10%-15%之间。&lt;/p&gt;&lt;p&gt;　　随着消费者的咖啡消费品位越来越高。消费者开始认知咖啡的品牌、风格和纯正度，并且希望享受咖啡带来的乐趣。办公、家庭咖啡机的普及，为消费者的咖啡豆消费提供了便利条件。&lt;/p&gt;&lt;p&gt;　　此外，大型咖啡连锁机构纷纷进入中国，如星巴克、CAFFE PASCUCCI、上岛、漫咖啡等。国内本土咖啡店也在崛起之中，咖啡店在国内大部分城市得到了发展，各种促销、广告、公关传播活动的开展，使得咖啡消费人群增加，地域扩大。&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;', '1527672999', '1527672999');
INSERT INTO `ey_article_content` VALUES ('32', '57', '&lt;p&gt;2018年，咖啡变成了很多人眼里的好生意、好商机。这一年，咖啡领域群雄逐鹿，硝烟四起，各种营销活动充斥朋友圈，嗅觉敏锐的资本也不断涌入，咖啡行业真的要进入黄金爆发期了吗?&lt;/p&gt;&lt;p&gt;　　今年，不少微信群开始不断有人发送红包，它不来自美团外卖，也不来自滴滴出行，而是来自咖啡外卖品牌“连咖啡”。&lt;/p&gt;&lt;p&gt;　　“连咖啡”迅速在微信里爆发，不仅引来了用户，也吸引到了资本的关注。如今，连咖啡已于今年3月宣布获得1.58亿元B+轮融资，由启明创投领投、高榕资本跟投。&lt;/p&gt;&lt;p&gt;　　而真正引爆咖啡领域的是去年11月横空出世的瑞幸咖啡，由具有神州“运营一姐”之称的神州优车COO、董事钱治亚，带资10亿入场，成为很多人眼里的“搅局者”。不仅斥资3亿投入广告铺设，请来汤唯、张震代言，还采用高举高打的方式，同时拓展线下实体铺设+线上外卖模式运营，在短短6个月的时间里，开设店面525家，其中400多家在运营。&lt;/p&gt;&lt;p&gt;　　截止5月，瑞幸咖啡共覆盖100万用户，订单量达300万次，累计销量500万杯咖啡。&lt;/p&gt;&lt;p&gt;　　根据启明创投透露的数据显示，投资连咖啡时，连咖啡每天订单量在2～3万杯，而近期已经上涨到每天10万杯，而星巴克在中国每天在100～120万杯，占到了星巴克的十分之一。&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;', '1527673033', '1527673033');
INSERT INTO `ey_article_content` VALUES ('33', '58', '&lt;p&gt;咖啡是一门好生意。据数据预测，2025年中国咖啡市场规模将超万亿。&lt;/p&gt;&lt;p&gt;　　这几年，咖啡市场创新业态频出，吸引大量资本不断涌入，直到2018年，一夕全面爆发。&lt;/p&gt;&lt;p&gt;　　群雄逐鹿，硝烟四起。谁是下一个王者?&lt;/p&gt;&lt;p&gt;　　在这场咖啡的持久战中，从保守观望到伺机而动，资本的嗅觉最为灵敏。&lt;/p&gt;&lt;p&gt;　　有人说，咖啡和酒是最合法的致幻剂(兴奋剂)，但餐饮圈内人士却认为咖啡馆和酒吧的生意最难做!咖啡馆，高房租、小众化、低单价、重口味、轻技术……酒吧，不多做赘述。&lt;/p&gt;&lt;p&gt;　　数据显示，全球有超过10亿人每天饮用咖啡，咖啡豆是世界上交易量排名第二的大宗商品(仅次于原油)。无论市场规模还是消费数据，都表明咖啡是一门大生意、好商机，然而天生爱逐利的资本对进入中国30年的咖啡市场一直持观望态度。&lt;/p&gt;&lt;p&gt;　　笔者梳理近十年咖啡行业的投融资事件发现，在2008年-2015年之间，资本在咖啡消费领域下注并不多，并且投资的都是咖啡馆连锁企业。2015年至今，咖啡市场频出创新业态，资本开始伺机而动，直到2018年，一夕之间爆发。&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;', '1527673052', '1527673052');

-- -----------------------------
-- Table structure for `ey_auth_role`
-- -----------------------------
DROP TABLE IF EXISTS `ey_auth_role`;
CREATE TABLE `ey_auth_role` (
  `id` int(10) unsigned NOT NULL AUTO_INCREMENT,
  `name` varchar(255) DEFAULT '' COMMENT '角色名',
  `pid` int(10) DEFAULT '0' COMMENT '父角色ID',
  `remark` text COMMENT '备注信息',
  `grade` smallint(5) unsigned NOT NULL DEFAULT '0' COMMENT '级别',
  `language` text COMMENT '多语言权限',
  `online_update` tinyint(1) unsigned NOT NULL DEFAULT '0' COMMENT '在线升级',
  `only_oneself` tinyint(1) unsigned NOT NULL DEFAULT '0' COMMENT '只看自己发布',
  `cud` varchar(255) DEFAULT '' COMMENT '增改删',
  `permission` text COMMENT '已允许的权限',
  `built_in` tinyint(1) DEFAULT '0' COMMENT '内置用户组，1表示内置',
