DROP TABLE IF EXISTS `ey_single_content`;
CREATE TABLE `ey_single_content` (
  `id` int(10) NOT NULL AUTO_INCREMENT,
  `aid` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '文档ID',
  `typeid` int(10) DEFAULT '0' COMMENT '栏目ID',
  `content` longtext COMMENT '内容详情',
  `add_time` int(11) DEFAULT '0' COMMENT '新增时间',
  `update_time` int(11) DEFAULT '0' COMMENT '更新时间',
  PRIMARY KEY (`id`),
  KEY `aid` (`aid`) USING BTREE
) ENGINE=MyISAM AUTO_INCREMENT=9 DEFAULT CHARSET=utf8;

-- -----------------------------
-- Records of `ey_single_content`
-- -----------------------------
INSERT INTO `ey_single_content` VALUES ('1', '1', '1', '&lt;p&gt;茶餐厅源于美食之都香港，香港这个国际大都市汇聚世界各地的美食，香港的旺角、铜锣湾、尖沙咀和九龙城尽是各色食肆，各地的特色佳肴，亚洲风味餐馆遍布，而缘起于香港的地道食肆茶餐厅更是随处可见，粤港便是将这种传统的香港味道带到中国大陆市场。&lt;br/&gt;粤港茶餐厅传自香港多年的粤港冰室,传承其对品质的坚持和苛求，经过长久的创新和研发，以香港人对食物的精益求精,营养和健康的饮食理念，保持食材原汁原味的烹饪方式,为全球数千万忠实消费者提供多样化的融合健康美食。&lt;br/&gt;粤港茶餐厅倡导一种由饮食带来的”漫生活””新时尚””享健康”的生活方式，在悠闲而时尚的粤港里，品味美食,让繁忙的都市生活稍稍停歇，享受轻松与自在!粤港，餐厅一直秉承顾客至上的原则，以其全新的模式和独特的经营理念，结合与时俱进的融合饮食潮流趋势，为消费者提供舒适的环境，经典的美食和一流的服务。&lt;br/&gt;让爱美食，爱健康，爱时尚的你找寻香港过往记忆，品尝各色融合菜系，演绎健康时尚漫生活。&lt;/p&gt;', '1540343643', '1540343643');
INSERT INTO `ey_single_content` VALUES ('8', '38', '38', '&lt;p&gt;地址：海南省海口市&lt;br/&gt;电话：0898-66969005&lt;br/&gt;手机：13988889999&lt;br/&gt;QQ：970003436&lt;br/&gt;邮箱：admineyoucms.com&lt;/p&gt;', '1527663497', '1527675708');

